  import React from 'react';
  import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';

  import { AboutUs, FindUs, Footer, Gallery, Header, Intro, SpecialMenu, Footer2, Spacer } from './container';
  import { Navbar } from './components';
  import Products from './pages/products/products'; // components should be PascalCase
  import './App.css';

  // Create a new component for the Home page
  const Home = ({ aboutRef, contactRef }) => (
    <>
       <Header />
    <AboutUs ref={aboutRef} />
    <SpecialMenu />
    <Intro />
    <Gallery />
    <FindUs ref={contactRef} />
    <Footer2/>
    <Spacer/>
    </>
  );

  // App.jsx dosyası
import { useRef } from 'react';

const App = () => {
  const aboutRef = useRef(null);
  const contactRef = useRef(null);

  return (
    <Router>
      <div className='bg'>
        <Navbar aboutRef={aboutRef} contactRef={contactRef} />
        <Routes>
          <Route path="/" element={<Home aboutRef={aboutRef} contactRef={contactRef} />} exact/>
          <Route path="/products" element={<Products/>} /> 
        </Routes>
        <Footer />
      </div>
    </Router>
  );
};


  export default App;
