import SubHeading from '../SubHeading/SubHeading';
import './Newsletter.css';

const Newsletter = () => {
  const [isClicked, setIsClicked] = useState(false);
  const [email, setEmail] = useState(''); // Yeni ekleme

  const handleClick = () => {
    setIsClicked(true);
    setTimeout(() => {
        setIsClicked(false);
    },1000); 

    console.log(email); // E-posta adresini kontrol edin
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value); // E-posta adresini duruma kaydet
  };

  return (
    <div className="app__newsletter">
      <div className="app__newsletter-heading">
        <SubHeading/>
        <h1 className="headtext__cormorant">BÃ¼ltenimize Abone Olun!</h1>
        <p className="p__opensans"></p>
      </div>
      <div className="app__newsletter-input flex__center">
        <input 
          type="email" 
          placeholder="Mail adresinizi giriniz" 
          value={email}
          onChange={handleEmailChange} 
        />
        <div className="wrap">
          <button 
            className={`button ${isClicked ? 'clicked' : ''}`} 
            onClick={handleClick}
          >
            Submit
          </button>
        </div>
      </div>
    </div>
  );
};

export default Newsletter;
