import React, { useEffect, useState } from 'react';
import './animation.css';
import Lottie from "lottie-react";
import squirrel from "../../assets/squirrel.json"

const Animation = () => {
    const [state, setState] = useState({position: 0, direction: 1, waiting: false, opacity: 1});
    const lottieSize = 150; // Lottie animasyonunun genişliğini ayarlayın.

    useEffect(() => {
        const interval = setInterval(() => {
            if(state.waiting) return;
            let newPosition = state.position + state.direction;
            if(newPosition > window.innerWidth && state.direction === 1) {
                setState({...state, position: newPosition, waiting: true, opacity: 0});
                setTimeout(() => {
                    setState({position: window.innerWidth, direction: -1, waiting: false, opacity: 1});
                }, Math.random() * 2000 + 1000);
            } else if (newPosition < -lottieSize && state.direction === -1) {
                setState({...state, position: newPosition, waiting: true, opacity: 0});
                setTimeout(() => {
                    setState({position: 0, direction: 1, waiting: false, opacity: 1});
                }, Math.random() * 2000 + 1000);
            } else {
                setState({...state, position: newPosition});
            }
        }, 3);
        return () => {
            clearInterval(interval);
        };
    }, [state]);

    return (
        <div className="animation-container">
            <div 
                className="squirrel" 
                style={{ 
                    position: "fixed",
                    left: `${state.position}px`,
                    transform: `scaleX(${state.direction})`,
                    opacity: `${state.opacity}`
                }}
            >
                <Lottie animationData={squirrel} />
            </div>
        </div>
    );
};

export default Animation;
