import React, { useState } from 'react';
import axios from 'axios';

const Newsletter = () => {
  const [email, setEmail] = useState('');
  const [isClicked, setIsClicked] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsClicked(true);
    setTimeout(() => {
      setIsClicked(false);
    }, 1000);

    try {
      const response = await axios({
        method: 'post',
        url: 'https://api.sendinblue.com/v3/contacts',
        headers: {
          'Content-Type': 'application/json',
          'api-key': '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', // Yukarıda oluşturduğunuz API Key
        },
        data: {
          email: email,
          listIds: [2], // Yukarıda oluşturduğunuz listenin ID'si
        },
      });

      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="app__newsletter">
      <div className="app__newsletter-heading">
        <h1 className="headtext__cormorant">Bültenimize Abone Olun!</h1>
        <p className="p__opensans"></p>
      </div>
      <div className="app__newsletter-input flex__center">
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            placeholder="Mail adresinizi giriniz"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <div className="wrap">
            <button 
              className={`button ${isClicked ? 'clicked' : ''}`}
              type="submit"
            >
              Submit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Newsletter;
