import { useEffect, useRef, useState } from 'react'
import gsap from 'gsap-trial'
import DrawSVGPlugin from 'gsap-trial/DrawSVGPlugin'
import NewComponent from './sign'

const Logo = () => {
  const bgRef = useRef()
  const outlineLogoRef = useRef()
  const solidLogoRef = useRef()
  const [key, setKey] = useState(0)
  const [loadComponent, setLoadComponent] = useState(false)

  const handleMouseEnter = () => {
    setKey(prevKey => prevKey + 1);
  }

    const handleLogoClick = () => {
      window.location.href = 'https://www.linkedin.com/in/mete-han-%C3%A7etiner-3534431a8/';
    }



  useEffect(() => {
    gsap.registerPlugin(DrawSVGPlugin)

    gsap
      .timeline()
      .to(bgRef.current, {
        duration: 1,
        opacity: 1,
      })
      .from(outlineLogoRef.current, {
        drawSVG: 1,
        duration: 15,
      })

    gsap.fromTo(
      solidLogoRef.current,
      {
        opacity: 0,
      },
      {
        opacity: 1,
        delay: 5,
        duration: 4,
      }
    )
    setTimeout(() => {
      setLoadComponent(true);
    }, 1000);
  }, [])

  return (
    <div className="logo-container" ref={bgRef} onClick={handleLogoClick}>
      <img
        className="solid-logo"
        ref={solidLogoRef}
      />
      <div onMouseEnter={handleMouseEnter}>
        {loadComponent && <div className="small-component"><NewComponent key={key} /></div>}
      </div>
    </div>
  )
}

export default Logo
