import SubHeading from '../SubHeading/SubHeading';
import './Newsletter.css';
import styled from 'styled-components';

const Wrapper = styled.div`
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
`;

const LinkWrapper = styled.div`
    position: relative;
`;

const Button = styled.a`
    display: block;
    width: 250px;
    height: 50px;
