    background: #333;
    text-align: center;
    color: #fff;
    text-transform: uppercase;
    letter-spacing: 1px;
    border: 3px solid #333;
    transition: all .35s;

    &:hover {
        width: 200px;
        border: 3px solid #2ecc71;
        background: transparent;
        color: #2ecc71;
    }

    & + .icon {
        border: 3px solid #2ecc71;
        right: -25%;
    }
`;

const Icon = styled.div`
    width: 50px;
    height: 50px;
    border: 3px solid transparent;
    position: absolute;
    transform: rotate(45deg);
    right: 0;
    top: 0;
    z-index: -1;
    transition: all .35s;

    svg {
        width: 30px;
        position: absolute;
        top: calc(50% - 15px);
        left: calc(50% - 15px);
        transform: rotate(-45deg);
        fill: #2ecc71;
        transition: all .35s;
    }
`;

