import React, { useState } from 'react';

import SubHeading from '../SubHeading/SubHeading';
import './Newsletter.css';

const Newsletter = () => {
  const [clicks, setClicks] = useState([]);

  const handleClick = () => {
    setClicks([...clicks, Date.now()]);
    setTimeout(() => {
      setClicks(clicks.filter((_, index) => index !== 0));
    }, 1000);
  };

  return (
    <div className="app__newsletter">
      <div className="app__newsletter-heading">
        <SubHeading/>
        <h1 className="headtext__cormorant">BÃ¼ltenimize Abone Olun!</h1>
        <p className="p__opensans"></p>
      </div>
      <div className="app__newsletter-input flex__center">
        <input type="email" placeholder="Mail adresinizi giriniz" />
        <div className="wrap">
          {clicks.map(click => (
            <button 
              key={click}
              className={`button clicked`}
              onClick={handleClick}
              style={{
                animation: `colorChange 1s linear infinite, ring-${click} 1s linear`
              }}
            >
              Submit
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Newsletter;
