import React, { useState } from 'react';

import SubHeading from '../SubHeading/SubHeading';
import './Newsletter.css';

const Newsletter = () => {

  const [animations, setAnimations] = useState([]);

  const handleClick = () => {
    setAnimations([...animations, { key: Date.now() }]);

    // Eğer çok fazla animasyon oluşturulursa, ilk eklenenlerin silinmesi için bir zaman aşımı belirleyebiliriz
    setTimeout(() => {
      setAnimations(animations.slice(1));
    }, 2000);
  };

  return (
    <div className="app__newsletter">
      <div className="app__newsletter-heading">
        <SubHeading/>
        <h1 className="headtext__cormorant">Bültenimize Abone Olun!</h1>
        <p className="p__opensans"></p>
      </div>
      <div className="app__newsletter-input flex__center">
        <input type="email" placeholder="Mail adresinizi giriniz" />
        <div className="wrap">
          {animations.map((animation) => (
            <button 
              key={animation.key}
              className="button clicked" 
              onAnimationEnd={() => {
                setAnimations(animations.filter(a => a.key !== animation.key));
              }}
            >
              Submit
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Newsletter;
