import React, { useEffect, useState } from 'react';
import Lottie from "lottie-react";
import squirrel from "../../assets/squirrel.json";
import './animation.css';

const Animation = () => {
  const initialSize = 100;
  const [state, setState] = useState({
    position: -initialSize,
    direction: 1
  });

  useEffect(() => {
    const interval = setInterval(() => {
      setState(prevState => {
        let newPosition = prevState.position + prevState.direction * 5;

        if (newPosition > window.innerWidth && prevState.direction === 1) {
          newPosition = -initialSize * 2;
        }

        if (newPosition < -initialSize && prevState.direction === -1) {
          newPosition = window.innerWidth + initialSize;
        }

        return { position: newPosition, direction: -prevState.direction };
      });
    }, 20);

    return () => clearInterval(interval);
  }, [initialSize]);

  return (
    <div className="animation-container">
      <div 
        className="squirrel" 
        style={{ 
          position: "absolute",
          transform: `translateX(${state.position}px) scaleX(${state.direction})`,
          width: `${initialSize}px`,
          height: 'auto'
        }}
      >
        <Lottie animationData={squirrel} />
      </div>
    </div>
  );
};

export default Animation;
