import React, { useState } from 'react';
import emailjs from 'emailjs-com';

import SubHeading from '../SubHeading/SubHeading';
import './Newsletter.css';

const Newsletter = () => {
  const [isClicked, setIsClicked] = useState(false);
  const [email, setEmail] = useState('');

  const handleClick = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_evahlbt', 'template_nzhklo8', e.target, '3stLvJAm6BvTLpIsx')
      .then((result) => {
        console.log(result.text);
        setEmail(''); // Formu temizle
      }, (error) => {
        console.log(error.text);
      });

    setIsClicked(true);
    setTimeout(() => {
        setIsClicked(false);
    },1000); 
  };

  return (
    <div className="app__newsletter">
      <div className="app__newsletter-heading">
        <SubHeading/>
        <h1 className="headtext__cormorant">BÃ¼ltenimize Abone Olun!</h1>
        <p className="p__opensans"></p>
      </div>
      <form onSubmit={handleClick} className="app__newsletter-input flex__center">
        <input 
          type="email" 
          name="email"
          value={email} 
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Mail adresinizi giriniz" 
          required
        />
        <div className="wrap">
          <button 
            className={`button ${isClicked ? 'clicked' : ''}`} 
          >
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default Newsletter;
