import React, { useEffect, useState } from 'react';
import Lottie from "lottie-react";
import squirrel from "../../assets/squirrel.json"
import './animation.css';

const Animation = () => {
    const initialSize = 100; // Sizin belirttiğiniz boyut
    const [state, setState] = useState({
        position: -initialSize,
        direction: 1,
        waiting: false,
    });

    useEffect(() => {
        const interval = setInterval(() => {
            if (state.waiting) return;
            let newPosition = state.position + state.direction * 5; // Hızı artırmak için 1'i 5 ile değiştirdim
            if (newPosition > window.innerWidth - initialSize && state.direction === 1) {
                setState(prevState => ({
                    ...prevState,
                    waiting: true,
                }));
                setTimeout(() => {
                    setState({
                        position: window.innerWidth - initialSize,
                        direction: -1,
                        waiting: false,
                    });
                }, Math.random() * 2000 + 1000);
            } else if (newPosition < -initialSize && state.direction === -1) {
                setState(prevState => ({
                    ...prevState,
                    waiting: true,
                }));
                setTimeout(() => {
                    setState({
                        position: -initialSize,
                        direction: 1,
                        waiting: false,
                    });
                }, Math.random() * 2000 + 1000);
            } else {
                setState(prevState => ({
                    ...prevState,
                    position: newPosition,
                }));
            }
        }, 20); // Animasyon hızını kontrol eder
        return () => clearInterval(interval);
    }, [state]);
    useEffect(() => {
        // Prevent body from scrolling horizontally
        const preventDefault = (e) => e.preventDefault();
        document.body.addEventListener('touchmove', preventDefault, { passive: false });

        return () => {
            document.body.removeEventListener('touchmove', preventDefault);
        };
    }, []);

    return (
        <div className="animation-container">
            <div 
                className="squirrel" 
                style={{ 
                    position: "absolute",
                    transform: `translateX(${state.position}px) scaleX(${state.direction})`,
                    width: `${initialSize}px`,
                    height: 'auto'
                }}
            >
                <Lottie animationData={squirrel} />
            </div>
        </div>
    );
};

export default Animation;
