import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { GiHamburgerMenu } from 'react-icons/gi';
import { MdOutlineRestaurantMenu } from 'react-icons/md';
import images from '../../constants/images';
import './Navbar.css';
import { ScrollContext } from '../context/scroll-context'; // Assuming you have a context that provides these refs

const Navbar = () => {
  const [toggleMenu, setToggleMenu] = React.useState(false);
  const { aboutRef, contactRef } = useContext(ScrollContext); // Fetch refs from context

  // Belirli bir ref'e scroll yapmayı sağlayan fonksiyon
  const scrollToRef = (ref) => window.scrollTo(0, ref.current.offsetTop);

  return (
    <nav className="app__navbar">
      <div className="app__navbar-logo">
        <img src={images.Logo} alt="header_img" />
      </div>
      <ul className="app__navbar-links">
        <li className="p__opensans"><Link to="/">Anasayfa</Link></li>
        <li className="p__opensans" onClick={() => scrollToRef(aboutRef)}>Hakkında</li>
        <li className="p__opensans"><Link to="/products">Ürünler</Link></li>
        <li className="p__opensans" onClick={() => scrollToRef(contactRef)}>İletişim</li>
      </ul>
      <div className="app__navbar-login">
        <a href="/#login" className="p__opensans">Kayıt</a>
        <div />
      </div>
      <div className="app__navbar-smallscreen">
        <GiHamburgerMenu color="#fff" fontSize={27} onClick={() => setToggleMenu(true)} />
        {toggleMenu && (
          <div className="app__navbar-smallscreen_overlay flex__center slide-bottom">
            <MdOutlineRestaurantMenu fontSize={27} className="overlay__close" onClick={() => setToggleMenu(false)} />
            <ul className="app__navbar-smallscreen_links">
              <li><Link to="/" onClick={() => setToggleMenu(false)}>Home</Link></li>
              <li onClick={() => { scrollToRef(aboutRef); setToggleMenu(false); }}>About</li>
              <li><Link to="/products" onClick={() => setToggleMenu(false)}>Menu</Link></li>
              <li onClick={() => { scrollToRef(contactRef); setToggleMenu(false); }}>Contact</li>
            </ul>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
